import { reqCartList, reqDelectCartById } from '@/api'

const state = {
  cartList: [],
}
const mutations = {
  GETCARTLIST(state, cartList) {
    state.cartList = cartList
  },
}
const actions = {
  async getCartList({ commit }) {
    let result = await reqCartList()
    //console.log(result)
    if (result.code == 200) {
      commit('GETCARTLIST', result.data)
    }
  },
  //删除购物车某一个产品
  async deleteCartListBySkuId({ commit }, skuId) {
    let result = await reqDelectCartById(skuId)
    console.log(result)
    if (result.code == 200) {
      return 'ok'
    } else {
      //代表删除失败
      return Promise.reject(new Error('faile'))
    }
  },
}
const getters = {
  cartList(state) {
    return state.cartList[0] || {}
  },
  /* cartInfoList(state){
    return state.
  } */
}

export default {
  state,
  mutations,
  actions,
  getters,
}
