import { reqCategory, reqGetBannerList } from '@/api/index.js'

//home的小仓库
const state = {
  categroyList: [],
  bannerList: [],
}
const mutations = {
  CATEGORYLIST(state, categroyList) {
    state.categroyList = categroyList
  },
}
const actions = {
  async categroyList({ commit }) {
    let result = await reqCategory()
    if (result.code == 200) {
      commit('CATEGORYLIST', result.data)
    }
  },
  //获取首页轮播图的数据
  async getBannerList() {
    let result = await reqGetBannerList()
    //console.log(result)
    if (result.code == 200) {
      commit()
    }
  },
}
const getters = {}

export default {
  state,
  mutations,
  actions,
  getters,
}
