//登陆注册模块
import { reqGetCode, reqRegister, reqUserLogin } from '@/api'
const state = {
  code: '',
}
const mutations = {
  GETCODE(state, code) {
    state.code = code
  },
}
const actions = {
  //获取验证码
  async getCode({ commit }, phone) {
    //获取验证码的接口 把验证码返回，正常情况是发到用户手机上
    let result = await reqGetCode(phone)
    //console.log(result)
    if (result.code == 200) {
      commit('GETCODE', result.data)
      return 'ok'
    } else {
      return Promise.reject(new Error('faile'))
    }
  },
  //用户注册
  async userRegister({ commit }, user) {
    let result = await reqRegister(user)
    //console.log(result)
    if (result.code == 200) {
      return 'ok'
    } else {
      return Promise.reject(new Error('faile'))
    }
  },
  //登陆 token
  async userLogin({ commit }, data) {
    let result = await reqUserLogin(data)
    console.log(result)
  },
}
const getters = {}

export default {
  state,
  mutations,
  actions,
  getters,
}
