//引用路由组件
import Home from '@/pages/Home/index.vue'
import Search from '@/pages/Search/index.vue'
import Login from '@/pages/Login/index.vue'
import Register from '@/pages/Register/index.vue'
import Detail from '@/pages/Detail'
//路由配置
export default [
  //重定向
  {
    path: '/',
    redirect: '/home',
  },
  {
    path: '/home',
    component: Home,
    //路由元信息--存储是否显示的信息--用于控制footer
    meta: { show: true },
  },
  {
    path: '/search/:keyword?',
    component: Search,
    meta: { show: true },
    name: 'search',
  },
  {
    path: '/login',
    component: Login,
    meta: { show: false },
  },
  {
    path: '/register',
    component: Register,
    meta: { show: false },
  },
  {
    path: '/detail/:skuid',
    component: Detail,
    //路由元信息--存储是否显示的信息--用于控制footer
    meta: { show: true },
  },
]
