//配置路由的地方
import Vue from 'vue'
import VueRouter from 'vue-router'

//作为插件
Vue.use(VueRouter)

//引用路由组件
import Home from '@/pages/Home/index.vue'
import Search from '@/pages/Search/index.vue'
import Login from '@/pages/Login/index.vue'
import Register from '@/pages/Register/index.vue'

//配置路由
export default new VueRouter({
  //配置路由
  routes: [
    //重定向
    {
      path: '/',
      redirect: '/home',
    },
    {
      path: '/home',
      component: Home,
      //路由元信息
      meta: { show: true },
    },
    {
      path: '/search',
      component: Search,
      meta: { show: true },
    },
    {
      path: '/login',
      component: Login,
      meta: { show: false },
    },
    {
      path: '/register',
      component: Register,
      meta: { show: false },
    },
  ],
})
